$(document).ready(function(){var n=$("#baseurl").attr("href");$(".tr-search").each(function(){var i=$(this).children()[0],t;$(this).click(function(){location.replace(n+"?q="+i.innerText)});t=document.createElement("a");t.href=n+"?q="+$(this).children()[0].innerText;$(this).mouseover(function(){$(".text-center").first().append(t);$(t).focus()});$(this).mouseleave(function(){$(t).remove()})})});